{
    "tiddlers": {
        "$:/themes/can/nothing/colours": {
            "title": "$:/themes/can/nothing/colours",
            "tags": "",
            "text": "
.tc-page-container {
background-attachment: fixed;
background-image: linear-gradient(18deg, #107227 0%, #0c2e52 35%, #0c2e52 50%, #107227 100%);
}



.tc-sidebar-tab-open-item {
    background-colour: <<colour background>>;
}

.tc-tab-content { 
    background-color: <<colour background>>;
}"
        },
        "$:/themes/can/nothing/base": {
            "title": "$:/themes/can/nothing/base",
            "tags": "$:/tags/Stylesheet",
            "text": "
/* Set padding on tiddlers */
.tc-tiddler-frame {
    padding: 28px 42px 16px 42px;
}


/* Button styling */
.tc-btn-invisible {
	padding: 0;
	margin: 0;
	background: none;
	border: none;
    cursor: pointer;
	color: <<colour foreground>>;
}

button svg.tc-image-button, button .tc-image-button img {
	height: 1em;
	width: 1em;
}

.tc-tiddler-title-icon {
	vertical-align: middle;
	margin-right: .1em;
}

.tc-sidebar-tabs-main {
/*margin-right: 1em;*/
}

.tc-tab-content {
/*margin-top: 0;
padding: 10px 0.4em 1em 0.4em;*/
}

.tc-tabbed-table-of-contents {
	display: -webkit-flex;
	display: flex;
}

.tc-tabbed-table-of-contents .tc-table-of-contents {
	z-index: 100;
	display: inline-block;
	padding-left: 1em;
	max-width: 50%;
	-webkit-flex: 0 0 auto;
	flex: 0 0 auto;
}

@media (min-width: {{$:/themes/can/nothing/metrics/sidebarbreakpoint}}) {

.tc-sidebar-scrollable {
		position: fixed;
		top: {{$:/themes/can/nothing/metrics/storytop}};
		left: {{$:/themes/can/nothing/metrics/storyright}};
		bottom: 0;
		right: 0;
		overflow-y: auto;
		overflow-x: auto;
		-webkit-overflow-scrolling: touch;
		margin: 0 0 0 -42px;
		padding: 71px 0 28px 42px;
	}


html[dir="rtl"] .tc-sidebar-scrollable {
		left: auto;
		right: {{$:/themes/can/nothing/metrics/storyright}};
	}

	.tc-story-river {
		position: relative;
		left: {{$:/themes/can/nothing/metrics/storyleft}};
		top: {{$:/themes/can/nothing/metrics/storytop}};
		width: {{$:/themes/can/nothing/metrics/storywidth}};
		padding: 42px 42px 42px 42px;
	}

<<if-no-sidebar "

	.tc-story-river {
		width: calc(100% - {{$:/themes/can/nothing/metrics/storyleft}});
	}

">>

}"
        },
